import { initMixin } from './init'
import { stateMixin } from './state'
import { renderMixin } from './render'
import { eventsMixin } from './events'
import { lifecycleMixin } from './lifecycle'
import { warn } from '../util/index'
// Vue构造函数，通过new Vue(options)来创建vue实例
function Vue (options) {
  // 如果不是生产环境并且不是通过new关键字调用，给出警告提示
  if (process.env.NODE_ENV !== 'production' &&
    !(this instanceof Vue)
  ) {
    warn('Vue is a constructor and should be called with the `new` keyword')
  }
  // 调用内部初始化方法
  this._init(options)
}
// 添加创建方法，即init
initMixin(Vue) 
// 添加对data/prop等实例跟处理方法
stateMixin(Vue)
// 添加（自定义）事件的绑定、触发、移除方法
eventsMixin(Vue)
// 添加生命周期内部执行的一些方法，如$forceUpdate、$destroy、内部update方法等
lifecycleMixin(Vue)
renderMixin(Vue) // 添加渲染方法

export default Vue
