import { inBrowser } from './env'

export let mark
export let measure
// window.performanc对象包含测量网页跟Web应用程序性能的方法
// window.performanc.mark()用来多次记录时间
// window.preformanc.measure()用来计算两个mark之间的时长
// window.preformanc.clearMarks()用来移除缓存中所有entryType为mark的数据
// window.preformanc.clearMeasures()用来移除缓存中所有entrytype为measure的数据
// 总的来说就是判断测试性能的各个方法是否存在，
// 存在的话
// mark就是一个用来记录时间的函数，记录值以键值对的形式保存在哈希表中
// 而measure就是用来计算两个mark之间时长的函数，计算比较完之后清除数据
if (process.env.NODE_ENV !== 'production') {
  const perf = inBrowser && window.performance
  /* istanbul ignore if */
  if (
    perf &&
    perf.mark &&
    perf.measure &&
    perf.clearMarks &&
    perf.clearMeasures
  ) {
    mark = tag => perf.mark(tag)
    measure = (name, startTag, endTag) => {
      perf.measure(name, startTag, endTag)
      perf.clearMarks(startTag)
      perf.clearMarks(endTag)
      perf.clearMeasures(name)
    }
  }
}
