/* @flow */

import { cached, extend, toObject } from 'shared/util'
// 解析style文本内容,正确的说是解析静态style的文本内容
export const parseStyleText = cached(function (cssText) {
  const res = {}
  // style各个属性的分隔符，即style里的属性以 ; 分隔，后面不能跟 ) 或除了 ( 外的字符
  const listDelimiter = /;(?![^(]*\))/g
  // style属性名跟属性值的分隔符
  const propertyDelimiter = /:(.+)/
  // 即先从静态style文本中解析分离出各个属性,然后再对每个属性做处理
  cssText.split(listDelimiter).forEach(function (item) {
    // 防止;;;这样的情况出现，对item存在与否做下判断
    if (item) {
      // 分离属性的属性名跟属性值
      var tmp = item.split(propertyDelimiter)
      // 若分离成功，以键值对的形式存到res中
      tmp.length > 1 && (res[tmp[0].trim()] = tmp[1].trim())
    }
  })
  // 返回处理后的结果（经过这样的处理会过滤掉多余的;）
  return res
})

// merge static and dynamic style data on the same vnode
// 在同一抽象节点上合并静态跟动态样式数据
function normalizeStyleData (data: VNodeData): ?Object {
  const style = normalizeStyleBinding(data.style)
  // static style is pre-processed into an object during compilation
  // and is always a fresh object, so it's safe to merge into it
  return data.staticStyle
    ? extend(data.staticStyle, style)
    : style
}

// normalize possible array / string values into Object
export function normalizeStyleBinding (bindingStyle: any): ?Object {
  if (Array.isArray(bindingStyle)) {
    return toObject(bindingStyle)
  }
  if (typeof bindingStyle === 'string') {
    return parseStyleText(bindingStyle)
  }
  return bindingStyle
}

/**
 * parent component style should be after child's
 * so that parent component's style could override it
 */
export function getStyle (vnode: VNodeWithData, checkChild: boolean): Object {
  const res = {}
  let styleData

  if (checkChild) {
    let childNode = vnode
    while (childNode.componentInstance) {
      childNode = childNode.componentInstance._vnode
      if (
        childNode && childNode.data &&
        (styleData = normalizeStyleData(childNode.data))
      ) {
        extend(res, styleData)
      }
    }
  }

  if ((styleData = normalizeStyleData(vnode.data))) {
    extend(res, styleData)
  }

  let parentNode = vnode
  while ((parentNode = parentNode.parent)) {
    if (parentNode.data && (styleData = normalizeStyleData(parentNode.data))) {
      extend(res, styleData)
    }
  }
  return res
}

